import React, { useState } from "react";
import List from "./list";

function APP() {
let [currentli,setCurrentli]=useState();
const [itemsli,setItemsli]=useState([]);

function getValue(){
    let value=document.getElementById("item").value
    setCurrentli(value);
}
function add(){

    if(!currentli){
        alert("please enter first");
    }
    else{
        setItemsli((old)=>{
            return(
                [...old,currentli]
            )
         })
         setCurrentli("");
         document.getElementById("item").value="";
         
    }
    
    
}
function deletefunc(id){
    console.log()
    setItemsli.
    itemsli.filter((a,index)=>{
        return index!==id;
    })
}
    return (
        <div className="container">
            <div className="center">
                <h1>ToDO List</h1>
                <input type="text" id="item" placeholder="Add a item" onChange={getValue} ></input>
                <button className="add" onClick={add}>+</button>

                <ul>
                    {
                        itemsli.map((li,index)=>{

                           return( 
                           <List text={li} key={index} id={index} onselect={deletefunc}></List>
                           )
                        })
                    }
                </ul>

            </div>
        </div>
    )
}

export default APP;
