import React from "react";
import {List,Listadd} from "./list";

function APP(){
    function add(event){
        let listitem=document.getElementById("item").value;
        
      }
    return(
       <div className="container">
           <div className="center">
               <h1>ToDO List</h1>
               <input type="text" id="item" placeholder="Add a item"></input>
               <button className="add" onClick={add}>+</button>

<ul>
<List listitem={listitem} ></List>
</ul>
               
           </div>
       </div>
    )
}

export default APP;
