import React, { useState } from 'react'
import Card from '../UI/Card';
import style from "../UI/Card.module.css"
import Button from '../UI/Button';
import ErrorModel from '../UI/ErrorModel';



function AddUser(props) {
    const [username,setUserName]=useState("");
    const [userage,setUserAge]=useState("");
    const [error,setError]=useState("");
   

    function onChangeUser(event){
        setUserName(event.target.value);
     }

     function onChangeAge(event){
        setUserAge(event.target.value);
     }


     function submitHandler(event){
        event.preventDefault();
        if((username.trim().length===0 || userage.trim().length===0)  || userage<0){
            setError({
                title:"Invalid Name and Age",
                message:"Please enter valid age"
            })
            return;
        }
        console.log(username,userage);
        props.onaddUsers(username,userage);
        setUserAge("");
        setUserName("");
       
    
    }
    
  return (
      <div> 
          
     { error && <ErrorModel title="Error Message" message="Please Try Again"></ErrorModel>}
      <Card classname={style.input}>
    <form onSubmit={submitHandler}>
        <label htmlFor='username'>Username</label>
        <input id="username" type="text" onChange={onChangeUser} value={username}></input>
        <label htmlFor='age'>Age</label>
        <input id="age" type="number" onChange={onChangeAge} value={userage}></input>
        <Button type="submit" handlesubmit={submitHandler}>Submit</Button>
    </form>
      </Card>
      </div>
  )
}

export default AddUser
