import React, { useState } from 'react'
import Card from '../UI/Card';
import style from "../UI/Card.module.css"
import Button from '../UI/Button';

let user,age="";
function submitHandler(event){
    event.preventDefault();
    console.log("hello");

}


function AddUser() {
    const [username,setUserName]=useState("");
    const [userage,setUserAge]=useState("");
   

    function onChangeUser(event){
        user=event.target.value;
        setUserName(user);
     }
     


    
  return (
      <Card classname={style.input}>
    <form onSubmit={submitHandler}>
        <label htmlFor='username'>Username</label>
        <input id="username" type="text" onChange={onChangeUser}></input>
        <label htmlFor='age'>Age</label>
        <input id="age" type="number" onChange={onChangeAge}></input>
        <Button type="submit" handlesubmit={submitHandler}>Submit</Button>
    </form>
      </Card>
  )
}

export default AddUser
