import React, { useState } from "react";


function APP() {
const [currentli,setCurrentli]=useState();
const [itemsli,setItemsli]=useState([]);

function getValue(){
    let value=document.getElementById("item").value
}
function add(){
    let value=document.getElementById("item").value;
    setCurrentli(value);

    setItemsli((old)=>{
       return(
           [...old,currentli]
       )
    })
    document.getElementById("item").value="";
}
    return (
        <div className="container">
            <div className="center">
                <h1>ToDO List</h1>
                <input type="text" id="item" placeholder="Add a item" onChange={getValue} ></input>
                <button className="add" onClick={add}>+</button>

                <ul>
                    {
                        itemsli.map((li)=>{
                           return(
                               <li> {li} </li>
                           )
                        })
                    }
                </ul>

            </div>
        </div>
    )
}

export default APP;
