import React, { useState } from 'react'
import "./index.css";
function App() {
  const [city,setcity]=useState({name:"",city:""});


  const getData=async(city_name)=>{
    try{
      const response=await fetch(`http://api.weatherapi.com/v1/current.json?key=becf17f5d71f4c2886e110734221006&q=${city_name}&aqi=no`);
      const data= await response.json();



     setcity((prev)=>{
       return(
         prev.name=data.current.temp_c,
         prev.city=data.location.name
       )
     })


    }

    catch(error){
      // console.log(error);
    }
  }
getData();

const submit=(event)=>{
 getData(event.target.value)
}

  return (
     <div className="Wheather-app">
       <div className="box">
       <input type="search" className='search' onChange={submit}></input>
       <div className="fetching-info">
         <h2> City:{city.name}</h2>
         <h4> Temperature:{city.temp}</h4>
       </div>
       </div>
     </div>
  )
}

export default App
