import React, { useEffect, useState } from 'react'
import Buttons from './Buttons';
import "./Clock.css";
function Clock() {


const [cycles,setCycles]=useState(0);
const [start,setStart]=useState(false);
const [pause,setPause]=useState(true);
const [hours,setHours]=useState(0);
const [min,setMin]=useState(0);
const [sec,setSec]=useState(0);
let minlimit;
let hourlimit;

const startfunc=()=>{
    if(cycles==0 || !cycles){
        alert("Please Enter Interval");
        return;
    }
    if(cycles%2===0){
       hourlimit=cycles/2;
       console.log(hourlimit);
      
    }
    else{

    }
     setStart(true);
    
    
}

const pausefunc=()=>{
  setPause((p)=>{
    return !p;
  })
}

const stopfunc=()=>{
  setStart(false);
  setSec((p)=>{return 0});
  setMin((p)=>{return 0});
  setHours((p)=>{return 0});

}

useEffect(()=>{


  if(start && pause){
    const interval=setInterval(()=>{
      
      if(sec<9){
        setSec((p)=>{

         
          return p+1
        })
      }
      else{
        setSec((p)=>{
          return 0;
        })
        setMin((m)=>{
          return m+1;
        })
      }
      if(min>9){
          setHours((p)=>{
            return p+1;
          })
          setMin((p)=>{
            return 0;
          })
      }
      
      },1000);
      
      return()=>clearInterval(interval);
  }
},[start,sec,min,pause])
 



    

  
  return (
      <div className='clock-info'>
      <h1 className='currenttitle'>Focus</h1>
    <div className='clock'>{start? `${hours}:${min}:${sec}` :`0:00:00`} </div>
    <input type="number" placeholder='enter intervals' onChange={(e)=>{setCycles(e.target.value)}}></input>
    <div className="buttons">
        <button className='startBtn' onClick={startfunc}>Start</button>
        <button className="stopBtn" onClick={pausefunc}>Pause</button>
        <button className='resetBtn' onClick={stopfunc}>Reset</button>
    </div>
      </div>

    
  )
}

export default Clock
