import React, { useEffect, useState } from 'react'
import Buttons from './Buttons';
import "./Clock.css";
function Clock() {


const [cycles,setCycles]=useState(0);
const [start,setStart]=useState(false);
const [hours,setHours]=useState(0);
const [min,setMin]=useState(0);
const [sec,setSec]=useState(0);

const startfunc=()=>{
    if(cycles==0 || !cycles){
        alert("Please Enter Interval");
        return;
    }
     setStart(true);
    
    
}

useEffect(()=>{

  if(start){
    const interval=setInterval(()=>{
      {sec>60 ? setSec(0)  :setSec((s)=>s+1)}
      console.log(sec);
      },1000);
      return()=>clearInterval(interval);
  }
},[start,sec,min])


    

  
  return (
      <div className='clock-info'>
      <h1 className='currenttitle'>Focus</h1>
    <div className='clock'>{start? `${hours}:${min}:${sec}` :`0:00:00`} </div>
    <input type="number" placeholder='enter intervals' onChange={(e)=>{setCycles(e.target.value)}}></input>
    <div className="buttons">
        <button className='startBtn' onClick={startfunc}>Start</button>
        <button className="stopBtn">Stop</button>
        <button className='resetBtn'>Reset</button>
    </div>
      </div>

    
  )
}

export default Clock
