import React, { useRef, useState } from 'react'
import Card from '../UI/Card';
import style from "../UI/Card.module.css"
import Button from '../UI/Button';
import ErrorModel from '../UI/ErrorModel';



function AddUser(props) {
    // const [username,setUserName]=useState("");
    // const [userage,setUserAge]=useState("");
    const [error,setError]=useState("");
    const usernameRef=useRef();
    const userageRef=useRef();

   

    function onChangeUser(event){
        setUserName(event.target.value);
     }

     function onChangeAge(event){
        setUserAge(event.target.value);
     }


     function submitHandler(event){
        event.preventDefault();
    const newNamewithRef=usernameRef.current.value;
    const newAgewithRef=userageRef.current.value;
        if((newNamewithRef.trim().length===0 || newAgewithRef.trim().length===0)  || newAgewithRef<0){
            setError({
                title:"Invalid Name and Age",
                message:"Please enter valid age"
            })
            return;
        }
       
        props.onaddUsers(newNamewithRef,newAgewithRef);

        // setUserAge("");
        // setUserName("");
       
    
    }
    function onConfirm(){
        
        setError("");
    }
    
  return (
      <div> 
          
     { error && <ErrorModel title={error.title} message={error.message} confirm ={onConfirm}></ErrorModel>}
      <Card classname={style.input}>
    <form onSubmit={submitHandler}>
        <label htmlFor='username'>Username</label>
        <input id="username" ref={usernameRef} type="text" onChange={onChangeUser} value={username}></input>
        <label htmlFor='age'>Age</label>
        <input id="age" type="number"  ref={userageRef} onChange={onChangeAge} value={userage}></input>
        <Button type="submit" onClick={submitHandler}>Submit</Button>
    </form>
      </Card>
      </div>
  )
}

export default AddUser
