import {React,ReactDOM} from 'react'
import styles from "./ErrorModel.module.css"; 
import Card from './Card';
import Button from './Button';

function backdrop(props){
   return   <div className={styles.backdrop} onClick={props.confirm}></div>
}

function Overlay(props){
  return(
    <Card classname={styles.modal}>
            <header className={styles.header} >
             <h2>{props.title}</h2>
            </header>
            <div className={styles.content}>
                <p>{props.message}</p>
            </div>
            <footer className={styles.actions}>
           <Button onClick={props.confirm}>Okay</Button>
            </footer>
     </Card>
  )
}
function ErrorModel(props) {
  return (
        <React.Fragment>
          {ReactDOM.createPortal(<backdrop confirm={props.confirm}/>,document.getElementById("backdrop-root"))};
          {ReactDOM.createPortal(<overlay title={props.title} message={props.message} confirm={props.confirm}/>,document.getElementById("overlay-root"))};
    </React.Fragment>
  )
}

export default ErrorModel
