import React, { useState } from 'react'
import Card from '../UI/Card';
import style from "../UI/Card.module.css"
import Button from '../UI/Button';



function AddUser(props) {
    const [username,setUserName]=useState("");
    const [userage,setUserAge]=useState("");
   

    function onChangeUser(event){
        setUserName(event.target.value);
     }

     function onChangeAge(event){
        setUserAge(event.target.value);
     }


     function submitHandler(event){
        event.preventDefault();
        if((username.trim().length===0 || userage.trim().length===0)  || userage<0){
            return;
        }
        console.log(username,userage);
        setUserAge("");
        setUserName("");
       
    
    }
    
  return (
      <Card classname={style.input}>
    <form onSubmit={submitHandler}>
        <label htmlFor='username'>Username</label>
        <input id="username" type="text" onChange={onChangeUser} value={username}></input>
        <label htmlFor='age'>Age</label>
        <input id="age" type="number" onChange={onChangeAge} value={userage}></input>
        <Button type="submit" handlesubmit={submitHandler}>Submit</Button>
    </form>
      </Card>
  )
}

export default AddUser
